mapper.select:
select  from tb_favor 
where user_id = #{userId,jdbcType=VARCHAR} 
and favor_id = #{favorId,jdbcType=VARCHAR}
and favor_type = #{favorType,jdbcType=VARCHAR}

mapper.delete:
update tb_favor set delete_flag=#{deleteFlag,jdbcType=VARCHAR} 
where user_id = #{userId,jdbcType=VARCHAR} 
and favor_id = #{favorId,jdbcType=VARCHAR}
and favor_type = #{favorType,jdbcType=VARCHAR}
